mapper.update:
UPDATE TB_ATTENDANCE_MANAGEMENT
SET ASSIGN_CODE = '2000'
WHERE ATTENDANCE_CODE = #{attendanceCode}

UPDATE TB_ATTENDANCE_MANAGEMENT
SET ASSIGN_CODE = '3000'
WHERE ATTENDANCE_CODE = #{attendanceCode}

mapper.select:

SELECT COUNT(*)
FROM TB_ATTENDANCE
WHERE EMP_ID = #{empId}
  AND today >= DATE_FORMAT(CURDATE(), '%Y-%m-01')
  AND today < DATE_FORMAT(DATE_ADD(CURDATE(), INTERVAL 1 MONTH), '%Y-%m-01');
